#!/usr/bin/env node

/**
 * Module dependencies.
 */

var program = require('commander');
var request = require('request');

program
  .version('0.0.1')
  .option('-t, --title <title>', 'Title')
  .option('-a, --artist <artist>', 'Artist')
  .parse(process.argv);

var url = require('url').format({ pathname: '/lyrics', protocol: 'http:', hostname: 'makeitpersonal.co', query: { title: program.title, artist: program.artist}});

request.get(url).pipe(process.stdout);
